document.addEventListener('DOMContentLoaded',function(){const tabButtons=document.querySelectorAll('.tab-btn');const tabPanes=document.querySelectorAll('.tab-pane');if(tabButtons.length>0&&tabPanes.length>0){console.log('Room tabs initialized');

// Show Standard room by default
const defaultTab = document.querySelector('.tab-btn[data-tab="standard"]');
if(defaultTab) {
    defaultTab.classList.add('tab-active');
    const defaultPane = document.getElementById('standard-content');
    if(defaultPane) {
        defaultPane.classList.add('active');
    }
}

tabButtons.forEach(button=>{button.addEventListener('click',function(){const tabId=this.getAttribute('data-tab');tabButtons.forEach(btn=>btn.classList.remove('tab-active'));this.classList.add('tab-active');tabPanes.forEach(pane=>pane.classList.remove('active'));const activePane=document.getElementById(`${tabId}-content`);if(activePane){activePane.classList.add('active');}});});}});
